(18, '把路由重写的算法撸出来了', '<td class="plc">\r\n<div class="pi">\r\n<div id="fj" class="y">\r\n<label class="z">电梯直达</label>\r\n<input type="text" class="px p_fre z" size="2" onkeyup="$(''fj_btn'').href=''forum.php?mod=redirect&amp;ptid=3618&amp;authorid=0&amp;postno=''+this.value" onkeydown="if(event.keyCode==13) {window.location=$(''fj_btn'').href;return false;}" title="跳转到指定楼层">\r\n<a href="javascript:;" id="fj_btn" class="z" title="跳转到指定楼层"><img src="http://www.kusha.biz/static/image/common/fj_btn.png" alt="跳转到指定楼层" class="vm"></a>\r\n</div>\r\n<strong>\r\n<a href="http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;fromuid=20" title="您的朋友访问此链接后，您将获得相应的积分奖励" id="postnum24752" onclick="setCopy(this.href, ''帖子地址复制成功'');return false;">楼主</a>\r\n</strong>\r\n<div class="pti">\r\n<div class="pdbt">\r\n</div>\r\n<div class="authi">\r\n<img class="authicn vm" id="authicon24752" src="http://www.kusha.biz/static/image/common/online_admin.gif">\r\n<em id="authorposton24752">发表于 2015-4-1 01:50:16</em>\r\n<span class="pipe">|</span><a href="http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;page=1&amp;authorid=20" rel="nofollow">只看该作者</a>\r\n<span class="pipe">|</span><a href="http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;extra=page%3D1&amp;ordertype=1">倒序浏览</a>\r\n<span class="pipe">|</span>\r\n<a id="replynotice" href="http://www.kusha.biz/forum.php?mod=misc&amp;action=replynotice&amp;op=ignore&amp;tid=3618" status="1" onclick="ajaxmenu(this, 3000, 0, 0, ''43'', function () {replyNotice();});return false;">取消回复通知</a>\r\n</div>\r\n</div>\r\n</div><div class="pct"><style type="text/css">.pcb{margin-right:0}</style><div class="pcb">\r\n<div class="t_fsz">\r\n<table cellspacing="0" cellpadding="0"><tbody><tr><td class="t_f" id="postmessage_24752">\r\n<img id="aimg_I9Z7e" onclick="zoom(this, this.src, 0, 0, 0)" class="zoom" src="http://i2.tietuku.com/c1b94d510569e268.jpg" onmouseover="img_onmouseoverfunc(this)" onload="thumbImg(this)" border="0" alt="" width="760" height="427" style="cursor: pointer;" initialized="true"><br>\r\n<br>\r\n<font size="7">楼顶喂鱼~~~</font><br>\r\n<br>\r\n<br>\r\n<br>\r\n<div align="left">define(''DI_ROUTE_REWRITE'', <strong>true</strong>); //启用路由重写</div><div align="left">define(''DI_KILL_ON_FAIL_REWRITE'', <strong>true</strong>);&nbsp;&nbsp;//未匹配重写时，是否终止程序</div><br>\r\n<div align="left"><strong>class </strong>DIRouteRewrite {</div><br>\r\n<div align="left">&nbsp; &nbsp; /**</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 自定义路由重写规则</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 书写原则，特殊在前，通用在后</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 详见：</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*&nbsp; &nbsp;&nbsp; &nbsp;DIRoute::rewrite() @ __route.php</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*/</div><div align="left">&nbsp; &nbsp; <strong>static </strong><i>$rulesMap</i> = <strong>array</strong>(</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''://danmucopy.me/t'' =&gt; ''test/test'', //含有域名的配置最好放在最前面(经验之谈)</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''://danmu.me/t'' =&gt; ''test/session'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''fm'' =&gt; ''fm/start'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''about'' =&gt; ''about'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''about.htm'' =&gt; ''about'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''about.html'' =&gt; ''about'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''s'' =&gt; ''shell.shell'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''s.html'' =&gt; ''shell.shell'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;nums&gt;'' =&gt; ''test/start/&lt;nums&gt;'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;'' =&gt; ''&lt;D&gt;/start'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;.htm'' =&gt; ''&lt;D&gt;/start'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;.html'' =&gt; ''&lt;D&gt;/start'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;/&lt;F&gt;'' =&gt; ''&lt;D&gt;/&lt;F&gt;'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;-&lt;F&gt;'' =&gt; ''&lt;D&gt;/&lt;F&gt;'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;.&lt;F&gt;'' =&gt; ''&lt;D&gt;.&lt;F&gt;'',</div><div align="left">&nbsp; &nbsp; );</div><br>\r\n<div align="left">&nbsp; &nbsp; /**</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 不需要重写的</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 左侧为相对于脚本目录的URI</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 右侧表示重写失败时是否终止程序</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 这些规则不受常量DI_KILL_ON_FAIL_REWRITE影响</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*/</div><div align="left">&nbsp; &nbsp; <strong>static </strong><i>$withoutMap</i> = <strong>array</strong>(</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''index.php'' =&gt; <strong>false</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''index.html'' =&gt; <strong>false</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''index.htm'' =&gt; <strong>false</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''favicon.ico'' =&gt; <strong>true</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''robots.txt'' =&gt; <strong>true</strong>,</div><div align="left">&nbsp; &nbsp; );</div><br>\r\n<br>\r\n<div align="left">&nbsp; &nbsp; /**</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 根据自定义规则重写路由。</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 不是所有规则都一定能实现，要看优先级。优先级以数组头部元素为最高</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* “&lt;&gt;”使用单个字母的，表示位置的代号，可实现规则的左右侧关系</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* &lt;num&gt;表示参数值为单数字，&lt;nums&gt;表示参数值为多个数字，&lt;letter&gt;表示参数值为单个字母，&lt;word&gt;表示参数值为多个字母，&lt;var&gt;表示参数值形似变量</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 当不确定需要使用多少参数时，可以考虑使用尾随参数&lt;*&gt;</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* &lt;*&gt;表示尾随参数，值类型任意，从左向右自动追加到规则右侧的尾部。追加尾随参数时，右侧用什么分隔符，就使用什么分隔符来分割参数。</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*/</div><div align="left">&nbsp; &nbsp; <strong>private function </strong>rewrite(){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$pureUri = ''/'' == path_prefix() ? uri_prefix() : uri_pure();</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>('''' === $pureUri) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>return</strong>;//请求中没有有用的URI</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$rules = DIRouteRewrite::<i>$rulesMap</i>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$dontRules = DIRouteRewrite::<i>$withoutMap</i>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$req = url_prefix(''://'') . $pureUri;</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//以下情况免重写</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>(in_array($pureUri, array_keys($dontRules))) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>if </strong>($dontRules[$pureUri]) <strong>die</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>return</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//开始尝试重写，遇到第一个符合的即确定结果</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>foreach </strong>($rules <strong>as </strong>$k =&gt; $rule) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$kBackup = $k;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>if </strong>(0 !== stripos($k, ''://'')) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; $k = ''://'' . $_SERVER[''HTTP_HOST''] . rtrim(dirname($_SERVER[''SCRIPT_NAME'']), ''\\//'') . ''/'' . $k;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$k = ''/'' . str_ireplace(</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>array</strong>(''\\\\\\\\'', ''://'', ''/'', ''&lt;'', ''&gt;'',&nbsp;&nbsp;''.'', ''*''),</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>array</strong>( '''', '''', ''\\/'', ''(?&lt;'', ''&gt;\\w+)?'', ''\\.'', ''tailparam''),</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; $k</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;) . ''\\/?$/i'';</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>if </strong>(preg_match($k, $req, $matchs)) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; $checkTypeInNeed = <strong>true</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>foreach</strong>($matchs <strong>as </strong>$matchkey =&gt; $matchval){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$isPosTag = is_letter($matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsLetter = 0 === strcasecmp(''letter'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsLetter = is_letter($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsWord = 0 === strcasecmp(''word'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsWord = is_word($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsNum = 0 === strcasecmp(''num'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsNum = is_a_num($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsNums = 0 === strcasecmp(''nums'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsNums = is_numeric($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsVar = 0 === strcasecmp(''var'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsVar = is_var($matchval);</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$typeIsNormal = $keyIsLetter &amp;&amp; $valueIsLetter || $keyIsWord &amp;&amp; $valueIsWord || $keyIsNum &amp;&amp; $valueIsNum || $keyIsNums &amp;&amp; $valueIsNums || $keyIsVar &amp;&amp; $valueIsVar;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>($isPosTag || $typeIsNormal) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$rule = str_ireplace("&lt;{$matchkey}&gt;", $matchval, $rule);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;} <strong>elseif </strong>(''tailparam'' === $matchkey) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$isDo = count(explode(''/'', $rule)) &gt;= 2;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$delimiter = $isDo ? ''/'' : ''.'';</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$rule .= $delimiter . $matchval;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//检查需要匹配类型时，是否匹配成功</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>($keyIsLetter&amp;&amp;!$valueIsLetter || $keyIsWord&amp;&amp;!$valueIsWord || $keyIsNum&amp;&amp;!$valueIsNum || $keyIsNums&amp;&amp;!$valueIsNums || $keyIsVar&amp;&amp;!$valueIsVar) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$checkTypeInNeed = <strong>false</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //通过$_SERVER[''REQUEST_URI'']的关键信息，以及上述规则，来重写$GLOBALS[''request_args'']的首索引，达到重写路由的目的</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //转化关系：a-b =&gt; a/b, a.b =&gt; a_b</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //使用a-b代替a/b，可以确保前端资源URL的正确性</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //a_b的下划线“_”与URL中QUERY_STRING参数名保持一致写法，在进入route()后，会自动转化为“.”</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>if </strong>($checkTypeInNeed) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$rule = str_replace(<strong>array</strong>(''-'', ''.''), <strong>array</strong>(''/'', ''_''), $rule);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;array_unshift_withkey($_REQUEST, $rule, '''');</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>return</strong>; //到此处为重写成功</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; }<strong>else </strong>{</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$kBackup = str_ireplace(<strong>array</strong>(''&lt;'',''&gt;''), <strong>array</strong>(''＜'', ''＞''), $kBackup);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$msg = "已匹配到重写规则，但URI中的个别部分或参数不与规则中的设定匹配。当前规则：[{$kBackup} =&gt; {$rule}]";</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>throw new </strong>Exception($msg);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//此处为无任何匹配时的处理。启用DI_KILL_ON_FAIL_REWRITE可以减少被盲点爬虫时消耗的流量</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>(DI_KILL_ON_FAIL_REWRITE) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>die</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp; }&nbsp;&nbsp;</div><br>\r\n<br>\r\n<div align="left">}&nbsp;&nbsp;</div><br>\r\n<br>\r\n<div align="left"><font color="black"><font face="&amp;quot;"><font style="font-size:24.0pt">以下是相关的辅助函数：</font></font></font></div><br>\r\n<div align="left">/**</div><div align="left"> * 获取当前执行的WEB目录前缀，截至当前执行目录，头部和尾部有“/”</div><div align="left"> * 例如：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;WEB根目录是 /usr/www/，要执行的脚本文件所在路径为/usr/www/aaa/index.php，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;则该脚本所在目录相对WEB根目录的路径值为：/aaa/，该值可由此方法得到。</div><div align="left"> */</div><div align="left">function path_prefix(){</div><div align="left">&nbsp; &nbsp; $di_sn = $_SERVER[''SCRIPT_NAME''];</div><div align="left">&nbsp; &nbsp; $di_num = strrpos($di_sn, ''/'');</div><div align="left">&nbsp; &nbsp; $di_ppr = substr($di_sn, 1, $di_num);//所在目录的相对路径，如“danmu/”</div><div align="left">&nbsp; &nbsp; return "/{$di_ppr}";</div><div align="left">}</div><div align="left">/**</div><div align="left"> * 获取$_SERVER[''REQUEST_URI'']中去除“QUERY_STRING”和前面“/”后的部分</div><div align="left"> * 例如：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;URL为<a href="http://127.0.0.1/shell.linenet?hehe.nima&amp;m=n!abc#a" target="_blank">http://127.0.0.1/shell.linenet?hehe.nima&amp;m=n!abc#a</a>，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;则URI为/shell.linenet?hehe.nima&amp;m=n!abc，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;处理后得到shell.linenet</div><div align="left"> */</div><div align="left">function uri_prefix(){</div><div align="left">&nbsp; &nbsp; $uri = $_SERVER[''REQUEST_URI''];</div><div align="left">&nbsp; &nbsp; $path_prefix = path_prefix();</div><div align="left">&nbsp; &nbsp; $len = false === ($len = strpos($uri, ''?'')) ? strlen($uri) : $len;</div><div align="left">&nbsp; &nbsp; $pre = strval(substr($uri, 1, $len - 1));</div><div align="left">&nbsp; &nbsp; return $pre;</div><div align="left">} </div><div align="left">/**</div><div align="left"> * 在uri_prefix()基础上，去除真实目录路径部分（当index.php不位于文档根目录时才需要用）</div><div align="left"> * 例如：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;URL为<a href="http://127.0.0.1/pub/danmu/shell.linenet?hehe.nima&amp;m=n!abc#a" target="_blank">http://127.0.0.1/pub/danmu/shell.linenet?hehe.nima&amp;m=n!abc#a</a>，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;则URI为/shell.linenet?hehe.nima&amp;m=n!abc，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;处理后得到shell.linenet</div><div align="left"> * 注意：该方法要配合.htaccess文件使用才有效。</div><div align="left"> * 具体如下：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;假定服务器文档根目录为/usr/www/</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;文件index.php为启动脚本，放置在虚拟相对于/usr/www/的虚拟目录pub/danmu/之下，可以视为/usr/www/pub/danmu/index.php</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;在虚拟目录中，pub/danmu/.htaccess文件的RewriteRule需要作特殊处理：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &lt;IfModule mod_rewrite.c&gt;</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteEngine on</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteBase /</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteCond %{REQUEST_FILENAME} !-f</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteCond %{REQUEST_FILENAME} !-d</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteRule . pub/danmu/index.php</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &lt;/IfModule&gt;</div><div align="left"> */</div><div align="left">function uri_pure(){</div><div align="left">&nbsp; &nbsp; $uri = $_SERVER[''REQUEST_URI''];</div><div align="left">&nbsp; &nbsp; $start = strlen(path_prefix());</div><div align="left">&nbsp; &nbsp; $len = false === ($len = strpos($uri, ''?'')) ? strlen($uri) : $len;</div><div align="left">&nbsp; &nbsp; $pure = strval(substr($uri, $start, $len - $start));</div><div align="left">&nbsp; &nbsp; return $pure;</div><div align="left">}</div><div align="left">//在array_unshift的基础上，对插入首部的元素带上自定义key</div><div align="left">function array_unshift_withkey(&amp;$arr, $key, $value){</div><div align="left">&nbsp; &nbsp; $backup = $arr;</div><div align="left">&nbsp; &nbsp; $arr = array($key =&gt; $value);</div><div align="left">&nbsp; &nbsp; foreach ($backup as $k =&gt; $v) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;if ($k == $key) continue;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$arr[$k] = $v;</div><div align="left">&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp; return count($arr);</div><div align="left">} </div><br>\r\n<br>\r\n<br>\r\n<font color="#000"><font color="#1e347b"><font color="#bebec5"><font color="#93a1a1">//是否为单个字母</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_letter</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">if</font><font color="#48484c"> </font><font color="#93a1a1">(</font><font color="#195f91">1</font><font color="#48484c"> </font><font color="#93a1a1">!=</font><font color="#48484c"> strlen</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">))</font><font color="#48484c"> </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">false</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; $o </font><font color="#93a1a1">=</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''A''</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> $o </font><font color="#93a1a1">&amp;&amp;</font><font color="#48484c"> $o </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''Z''</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">||</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''a''</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> $o </font><font color="#93a1a1">&amp;&amp;</font><font color="#48484c"> $o </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''z''</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<br>\r\n<font color="#bebec5"><font color="#93a1a1">//是否为单个数字</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_a_num</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">if</font><font color="#48484c"> </font><font color="#93a1a1">(</font><font color="#195f91">1</font><font color="#48484c"> </font><font color="#93a1a1">!=</font><font color="#48484c"> strlen</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">))</font><font color="#48484c"> </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">false</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> is_numeric</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">&amp;&amp;</font><font color="#48484c"> </font><font color="#195f91">1</font><font color="#48484c"> </font><font color="#93a1a1">==</font><font color="#48484c"> strlen</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<br>\r\n<font color="#bebec5"><font color="#93a1a1">//是否形似变量</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_var</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#93a1a1">!!</font><font color="#48484c"> preg_match</font><font color="#93a1a1">(</font><font color="#dd1144">''/^[a-z_](?:[a-z][a-z0-9_]*)*/is''</font><font color="#93a1a1">,</font><font color="#48484c"> $s</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<br>\r\n<font color="#bebec5"><font color="#93a1a1">//是否全为字母</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_word</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">foreach</font><font color="#48484c"> </font><font color="#93a1a1">((</font><font color="#48484c">array</font><font color="#93a1a1">)</font><font color="#48484c"> $s </font><font color="#1e347b">as</font><font color="#48484c"> $ss</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">{</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</font><font color="#1e347b">if</font><font color="#48484c"> </font><font color="#93a1a1">(!</font><font color="#48484c"> is_letter</font><font color="#93a1a1">(</font><font color="#48484c">$ss</font><font color="#93a1a1">))</font><font color="#48484c"> </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">false</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#93a1a1">}</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">true</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<font color="#93a1a1"><br>\r\n</font><br>\r\n</font><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<div align="left">/**</div><div align="left"> * 调用对象的方法</div><div align="left"> * @param object$obj 对象句柄</div><div align="left"> * @param string$method 方法名</div><div align="left"> * @param array$args 参数数组</div><div align="left"> */</div><div align="left">function invoke_method($obj, $method, $args=array()){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; if(! is_object($obj)){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;throw new DIException("{$obj}对象不存在或非对象类型");</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; if( method_exists($obj, $method) ){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;return call_user_func_array(array($obj, $method), $args);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; else if(array_key_exists($method, get_object_vars($obj))){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;if(is_callable(array($obj, $method))){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;call_user_func_array(array($obj, $method), $args);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}else{</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;throw new DIException(''成员方法访问操作：'' . get_class($obj) . '' -&gt; '' . $method . ''成员变量不具备callable特性，不能调用&lt;br&gt;'');</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; else{</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//如果成员不存在</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;throw new DIException(get_class($obj) . "对象的方法名[ {$method} ]不存在");</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">}</div><br>\r\n<br>\r\n<div align="left">执行入口：</div><br>\r\n<div align="left">DI_ROUTE_REWRITE &amp;&amp; invoke_method($r = <strong>new </strong>DIRouteRewrite, ''rewrite'');&nbsp;&nbsp;</div><br>\r\n<br>\r\n<br>\r\n<font color="#1e347b"><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<font size="2">本文转载须经作者{折木}同意&lt; <font color="#336699"><u>http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;page=1&amp;extra=#pid24752</u></font> &gt;</font><font size="1"><font color="#c0c0c0">(别看汉字没写多少，【【注释不是吗~~)</font></font><img src="http://www.kusha.biz/static/image/smiley/pansite/PST_119.gif" smilieid="105" border="0" alt=""><font size="1"><font color="#c0c0c0"> </font></font><br>\r\n<font color="#1e347b"><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<font color="#000"> </font><br>\r\n<font color="#1e347b"><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<font color="#000"><br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n</font><br>\r\n<br>\r\n</td></tr></tbody></table>\r\n</div>\r\n<div id="comment_24752" class="cm">\r\n</div>\r\n<div id="post_rate_div_24752"></div>\r\n</div></div>\r\n\r\n</td>', '<td class="plc">\r\n<div class="pi">\r\n<div id="fj" class="y">\r\n<label class="z">电梯直达</label>\r\n<input type="text" class="px p_fre z" size="2" onkeyup="$(''fj_btn'').href=''forum.php?mod=redirect&amp;ptid=3618&amp;authorid=0&amp;postno=''+this.value" onkeydown="if(event.keyCode==13) {window.location=$(''fj_btn'').href;return false;}" title="跳转到指定楼层">\r\n<a href="javascript:;" id="fj_btn" class="z" title="跳转到指定楼层"><img src="http://www.kusha.biz/static/image/common/fj_btn.png" alt="跳转到指定楼层" class="vm"></a>\r\n</div>\r\n<strong>\r\n<a href="http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;fromuid=20" title="您的朋友访问此链接后，您将获得相应的积分奖励" id="postnum24752" onclick="setCopy(this.href, ''帖子地址复制成功'');return false;">楼主</a>\r\n</strong>\r\n<div class="pti">\r\n<div class="pdbt">\r\n</div>\r\n<div class="authi">\r\n<img class="authicn vm" id="authicon24752" src="http://www.kusha.biz/static/image/common/online_admin.gif">\r\n<em id="authorposton24752">发表于 2015-4-1 01:50:16</em>\r\n<span class="pipe">|</span><a href="http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;page=1&amp;authorid=20" rel="nofollow">只看该作者</a>\r\n<span class="pipe">|</span><a href="http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;extra=page%3D1&amp;ordertype=1">倒序浏览</a>\r\n<span class="pipe">|</span>\r\n<a id="replynotice" href="http://www.kusha.biz/forum.php?mod=misc&amp;action=replynotice&amp;op=ignore&amp;tid=3618" status="1" onclick="ajaxmenu(this, 3000, 0, 0, ''43'', function () {replyNotice();});return false;">取消回复通知</a>\r\n</div>\r\n</div>\r\n</div><div class="pct"><style type="text/css">.pcb{margin-right:0}</style><div class="pcb">\r\n<div class="t_fsz">\r\n<table cellspacing="0" cellpadding="0"><tbody><tr><td class="t_f" id="postmessage_24752">\r\n<img id="aimg_I9Z7e" onclick="zoom(this, this.src, 0, 0, 0)" class="zoom" src="http://i2.tietuku.com/c1b94d510569e268.jpg" onmouseover="img_onmouseoverfunc(this)" onload="thumbImg(this)" border="0" alt="" width="760" height="427" style="cursor: pointer;" initialized="true"><br>\r\n<br>\r\n<font size="7">楼顶喂鱼~~~</font><br>\r\n<br>\r\n<br>\r\n<br>\r\n<div align="left">define(''DI_ROUTE_REWRITE'', <strong>true</strong>); //启用路由重写</div><div align="left">define(''DI_KILL_ON_FAIL_REWRITE'', <strong>true</strong>);&nbsp;&nbsp;//未匹配重写时，是否终止程序</div><br>\r\n<div align="left"><strong>class </strong>DIRouteRewrite {</div><br>\r\n<div align="left">&nbsp; &nbsp; /**</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 自定义路由重写规则</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 书写原则，特殊在前，通用在后</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 详见：</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*&nbsp; &nbsp;&nbsp; &nbsp;DIRoute::rewrite() @ __route.php</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*/</div><div align="left">&nbsp; &nbsp; <strong>static </strong><i>$rulesMap</i> = <strong>array</strong>(</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''://danmucopy.me/t'' =&gt; ''test/test'', //含有域名的配置最好放在最前面(经验之谈)</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''://danmu.me/t'' =&gt; ''test/session'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''fm'' =&gt; ''fm/start'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''about'' =&gt; ''about'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''about.htm'' =&gt; ''about'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''about.html'' =&gt; ''about'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''s'' =&gt; ''shell.shell'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''s.html'' =&gt; ''shell.shell'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;nums&gt;'' =&gt; ''test/start/&lt;nums&gt;'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;'' =&gt; ''&lt;D&gt;/start'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;.htm'' =&gt; ''&lt;D&gt;/start'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;.html'' =&gt; ''&lt;D&gt;/start'',</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;/&lt;F&gt;'' =&gt; ''&lt;D&gt;/&lt;F&gt;'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;-&lt;F&gt;'' =&gt; ''&lt;D&gt;/&lt;F&gt;'',</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''&lt;D&gt;.&lt;F&gt;'' =&gt; ''&lt;D&gt;.&lt;F&gt;'',</div><div align="left">&nbsp; &nbsp; );</div><br>\r\n<div align="left">&nbsp; &nbsp; /**</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 不需要重写的</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 左侧为相对于脚本目录的URI</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 右侧表示重写失败时是否终止程序</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 这些规则不受常量DI_KILL_ON_FAIL_REWRITE影响</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*/</div><div align="left">&nbsp; &nbsp; <strong>static </strong><i>$withoutMap</i> = <strong>array</strong>(</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''index.php'' =&gt; <strong>false</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''index.html'' =&gt; <strong>false</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''index.htm'' =&gt; <strong>false</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''favicon.ico'' =&gt; <strong>true</strong>,</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;''robots.txt'' =&gt; <strong>true</strong>,</div><div align="left">&nbsp; &nbsp; );</div><br>\r\n<br>\r\n<div align="left">&nbsp; &nbsp; /**</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 根据自定义规则重写路由。</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 不是所有规则都一定能实现，要看优先级。优先级以数组头部元素为最高</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* “&lt;&gt;”使用单个字母的，表示位置的代号，可实现规则的左右侧关系</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* &lt;num&gt;表示参数值为单数字，&lt;nums&gt;表示参数值为多个数字，&lt;letter&gt;表示参数值为单个字母，&lt;word&gt;表示参数值为多个字母，&lt;var&gt;表示参数值形似变量</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* 当不确定需要使用多少参数时，可以考虑使用尾随参数&lt;*&gt;</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;* &lt;*&gt;表示尾随参数，值类型任意，从左向右自动追加到规则右侧的尾部。追加尾随参数时，右侧用什么分隔符，就使用什么分隔符来分割参数。</div><div align="left">&nbsp; &nbsp;&nbsp;&nbsp;*/</div><div align="left">&nbsp; &nbsp; <strong>private function </strong>rewrite(){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$pureUri = ''/'' == path_prefix() ? uri_prefix() : uri_pure();</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>('''' === $pureUri) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>return</strong>;//请求中没有有用的URI</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$rules = DIRouteRewrite::<i>$rulesMap</i>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$dontRules = DIRouteRewrite::<i>$withoutMap</i>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$req = url_prefix(''://'') . $pureUri;</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//以下情况免重写</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>(in_array($pureUri, array_keys($dontRules))) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>if </strong>($dontRules[$pureUri]) <strong>die</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>return</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//开始尝试重写，遇到第一个符合的即确定结果</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>foreach </strong>($rules <strong>as </strong>$k =&gt; $rule) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$kBackup = $k;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>if </strong>(0 !== stripos($k, ''://'')) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; $k = ''://'' . $_SERVER[''HTTP_HOST''] . rtrim(dirname($_SERVER[''SCRIPT_NAME'']), ''\\//'') . ''/'' . $k;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$k = ''/'' . str_ireplace(</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>array</strong>(''\\\\\\\\'', ''://'', ''/'', ''&lt;'', ''&gt;'',&nbsp;&nbsp;''.'', ''*''),</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>array</strong>( '''', '''', ''\\/'', ''(?&lt;'', ''&gt;\\w+)?'', ''\\.'', ''tailparam''),</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; $k</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;) . ''\\/?$/i'';</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>if </strong>(preg_match($k, $req, $matchs)) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; $checkTypeInNeed = <strong>true</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>foreach</strong>($matchs <strong>as </strong>$matchkey =&gt; $matchval){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$isPosTag = is_letter($matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsLetter = 0 === strcasecmp(''letter'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsLetter = is_letter($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsWord = 0 === strcasecmp(''word'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsWord = is_word($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsNum = 0 === strcasecmp(''num'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsNum = is_a_num($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsNums = 0 === strcasecmp(''nums'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsNums = is_numeric($matchval);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$keyIsVar = 0 === strcasecmp(''var'', $matchkey);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$valueIsVar = is_var($matchval);</div><br>\r\n<div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$typeIsNormal = $keyIsLetter &amp;&amp; $valueIsLetter || $keyIsWord &amp;&amp; $valueIsWord || $keyIsNum &amp;&amp; $valueIsNum || $keyIsNums &amp;&amp; $valueIsNums || $keyIsVar &amp;&amp; $valueIsVar;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>($isPosTag || $typeIsNormal) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$rule = str_ireplace("&lt;{$matchkey}&gt;", $matchval, $rule);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;} <strong>elseif </strong>(''tailparam'' === $matchkey) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$isDo = count(explode(''/'', $rule)) &gt;= 2;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$delimiter = $isDo ? ''/'' : ''.'';</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$rule .= $delimiter . $matchval;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//检查需要匹配类型时，是否匹配成功</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>($keyIsLetter&amp;&amp;!$valueIsLetter || $keyIsWord&amp;&amp;!$valueIsWord || $keyIsNum&amp;&amp;!$valueIsNum || $keyIsNums&amp;&amp;!$valueIsNums || $keyIsVar&amp;&amp;!$valueIsVar) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;$checkTypeInNeed = <strong>false</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //通过$_SERVER[''REQUEST_URI'']的关键信息，以及上述规则，来重写$GLOBALS[''request_args'']的首索引，达到重写路由的目的</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //转化关系：a-b =&gt; a/b, a.b =&gt; a_b</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //使用a-b代替a/b，可以确保前端资源URL的正确性</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; //a_b的下划线“_”与URL中QUERY_STRING参数名保持一致写法，在进入route()后，会自动转化为“.”</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; <strong>if </strong>($checkTypeInNeed) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$rule = str_replace(<strong>array</strong>(''-'', ''.''), <strong>array</strong>(''/'', ''_''), $rule);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;array_unshift_withkey($_REQUEST, $rule, '''');</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>return</strong>; //到此处为重写成功</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; }<strong>else </strong>{</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$kBackup = str_ireplace(<strong>array</strong>(''&lt;'',''&gt;''), <strong>array</strong>(''＜'', ''＞''), $kBackup);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$msg = "已匹配到重写规则，但URI中的个别部分或参数不与规则中的设定匹配。当前规则：[{$kBackup} =&gt; {$rule}]";</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>throw new </strong>Exception($msg);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//此处为无任何匹配时的处理。启用DI_KILL_ON_FAIL_REWRITE可以减少被盲点爬虫时消耗的流量</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>if </strong>(DI_KILL_ON_FAIL_REWRITE) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;<strong>die</strong>;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp; }&nbsp;&nbsp;</div><br>\r\n<br>\r\n<div align="left">}&nbsp;&nbsp;</div><br>\r\n<br>\r\n<div align="left"><font color="black"><font face="&amp;quot;"><font style="font-size:24.0pt">以下是相关的辅助函数：</font></font></font></div><br>\r\n<div align="left">/**</div><div align="left"> * 获取当前执行的WEB目录前缀，截至当前执行目录，头部和尾部有“/”</div><div align="left"> * 例如：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;WEB根目录是 /usr/www/，要执行的脚本文件所在路径为/usr/www/aaa/index.php，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;则该脚本所在目录相对WEB根目录的路径值为：/aaa/，该值可由此方法得到。</div><div align="left"> */</div><div align="left">function path_prefix(){</div><div align="left">&nbsp; &nbsp; $di_sn = $_SERVER[''SCRIPT_NAME''];</div><div align="left">&nbsp; &nbsp; $di_num = strrpos($di_sn, ''/'');</div><div align="left">&nbsp; &nbsp; $di_ppr = substr($di_sn, 1, $di_num);//所在目录的相对路径，如“danmu/”</div><div align="left">&nbsp; &nbsp; return "/{$di_ppr}";</div><div align="left">}</div><div align="left">/**</div><div align="left"> * 获取$_SERVER[''REQUEST_URI'']中去除“QUERY_STRING”和前面“/”后的部分</div><div align="left"> * 例如：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;URL为<a href="http://127.0.0.1/shell.linenet?hehe.nima&amp;m=n!abc#a" target="_blank">http://127.0.0.1/shell.linenet?hehe.nima&amp;m=n!abc#a</a>，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;则URI为/shell.linenet?hehe.nima&amp;m=n!abc，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;处理后得到shell.linenet</div><div align="left"> */</div><div align="left">function uri_prefix(){</div><div align="left">&nbsp; &nbsp; $uri = $_SERVER[''REQUEST_URI''];</div><div align="left">&nbsp; &nbsp; $path_prefix = path_prefix();</div><div align="left">&nbsp; &nbsp; $len = false === ($len = strpos($uri, ''?'')) ? strlen($uri) : $len;</div><div align="left">&nbsp; &nbsp; $pre = strval(substr($uri, 1, $len - 1));</div><div align="left">&nbsp; &nbsp; return $pre;</div><div align="left">} </div><div align="left">/**</div><div align="left"> * 在uri_prefix()基础上，去除真实目录路径部分（当index.php不位于文档根目录时才需要用）</div><div align="left"> * 例如：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;URL为<a href="http://127.0.0.1/pub/danmu/shell.linenet?hehe.nima&amp;m=n!abc#a" target="_blank">http://127.0.0.1/pub/danmu/shell.linenet?hehe.nima&amp;m=n!abc#a</a>，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;则URI为/shell.linenet?hehe.nima&amp;m=n!abc，</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;处理后得到shell.linenet</div><div align="left"> * 注意：该方法要配合.htaccess文件使用才有效。</div><div align="left"> * 具体如下：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;假定服务器文档根目录为/usr/www/</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;文件index.php为启动脚本，放置在虚拟相对于/usr/www/的虚拟目录pub/danmu/之下，可以视为/usr/www/pub/danmu/index.php</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;在虚拟目录中，pub/danmu/.htaccess文件的RewriteRule需要作特殊处理：</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &lt;IfModule mod_rewrite.c&gt;</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteEngine on</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteBase /</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteCond %{REQUEST_FILENAME} !-f</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteCond %{REQUEST_FILENAME} !-d</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; RewriteRule . pub/danmu/index.php</div><div align="left"> *&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &lt;/IfModule&gt;</div><div align="left"> */</div><div align="left">function uri_pure(){</div><div align="left">&nbsp; &nbsp; $uri = $_SERVER[''REQUEST_URI''];</div><div align="left">&nbsp; &nbsp; $start = strlen(path_prefix());</div><div align="left">&nbsp; &nbsp; $len = false === ($len = strpos($uri, ''?'')) ? strlen($uri) : $len;</div><div align="left">&nbsp; &nbsp; $pure = strval(substr($uri, $start, $len - $start));</div><div align="left">&nbsp; &nbsp; return $pure;</div><div align="left">}</div><div align="left">//在array_unshift的基础上，对插入首部的元素带上自定义key</div><div align="left">function array_unshift_withkey(&amp;$arr, $key, $value){</div><div align="left">&nbsp; &nbsp; $backup = $arr;</div><div align="left">&nbsp; &nbsp; $arr = array($key =&gt; $value);</div><div align="left">&nbsp; &nbsp; foreach ($backup as $k =&gt; $v) {</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;if ($k == $key) continue;</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;$arr[$k] = $v;</div><div align="left">&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp; return count($arr);</div><div align="left">} </div><br>\r\n<br>\r\n<br>\r\n<font color="#000"><font color="#1e347b"><font color="#bebec5"><font color="#93a1a1">//是否为单个字母</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_letter</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">if</font><font color="#48484c"> </font><font color="#93a1a1">(</font><font color="#195f91">1</font><font color="#48484c"> </font><font color="#93a1a1">!=</font><font color="#48484c"> strlen</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">))</font><font color="#48484c"> </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">false</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; $o </font><font color="#93a1a1">=</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''A''</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> $o </font><font color="#93a1a1">&amp;&amp;</font><font color="#48484c"> $o </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''Z''</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">||</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''a''</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> $o </font><font color="#93a1a1">&amp;&amp;</font><font color="#48484c"> $o </font><font color="#93a1a1">&lt;=</font><font color="#48484c"> ord</font><font color="#93a1a1">(</font><font color="#dd1144">''z''</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<br>\r\n<font color="#bebec5"><font color="#93a1a1">//是否为单个数字</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_a_num</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">if</font><font color="#48484c"> </font><font color="#93a1a1">(</font><font color="#195f91">1</font><font color="#48484c"> </font><font color="#93a1a1">!=</font><font color="#48484c"> strlen</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">))</font><font color="#48484c"> </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">false</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> is_numeric</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">&amp;&amp;</font><font color="#48484c"> </font><font color="#195f91">1</font><font color="#48484c"> </font><font color="#93a1a1">==</font><font color="#48484c"> strlen</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<br>\r\n<font color="#bebec5"><font color="#93a1a1">//是否形似变量</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_var</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#93a1a1">!!</font><font color="#48484c"> preg_match</font><font color="#93a1a1">(</font><font color="#dd1144">''/^[a-z_](?:[a-z][a-z0-9_]*)*/is''</font><font color="#93a1a1">,</font><font color="#48484c"> $s</font><font color="#93a1a1">);</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<br>\r\n<font color="#bebec5"><font color="#93a1a1">//是否全为字母</font></font><br>\r\n<font color="#bebec5"><font color="#1e347b">function</font><font color="#48484c"> is_word</font><font color="#93a1a1">(</font><font color="#48484c">$s</font><font color="#93a1a1">){</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">foreach</font><font color="#48484c"> </font><font color="#93a1a1">((</font><font color="#48484c">array</font><font color="#93a1a1">)</font><font color="#48484c"> $s </font><font color="#1e347b">as</font><font color="#48484c"> $ss</font><font color="#93a1a1">)</font><font color="#48484c"> </font><font color="#93a1a1">{</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</font><font color="#1e347b">if</font><font color="#48484c"> </font><font color="#93a1a1">(!</font><font color="#48484c"> is_letter</font><font color="#93a1a1">(</font><font color="#48484c">$ss</font><font color="#93a1a1">))</font><font color="#48484c"> </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">false</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#93a1a1">}</font></font><br>\r\n<font color="#bebec5"><font color="#48484c">&nbsp; &nbsp; </font><font color="#1e347b">return</font><font color="#48484c"> </font><font color="#1e347b">true</font><font color="#93a1a1">;</font></font><br>\r\n<font color="#bebec5"><font color="#93a1a1">}</font></font><br>\r\n<font color="#93a1a1"><br>\r\n</font><br>\r\n</font><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<div align="left">/**</div><div align="left"> * 调用对象的方法</div><div align="left"> * @param object$obj 对象句柄</div><div align="left"> * @param string$method 方法名</div><div align="left"> * @param array$args 参数数组</div><div align="left"> */</div><div align="left">function invoke_method($obj, $method, $args=array()){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; if(! is_object($obj)){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;throw new DIException("{$obj}对象不存在或非对象类型");</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; if( method_exists($obj, $method) ){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;return call_user_func_array(array($obj, $method), $args);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; else if(array_key_exists($method, get_object_vars($obj))){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;if(is_callable(array($obj, $method))){</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;call_user_func_array(array($obj, $method), $args);</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}else{</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;throw new DIException(''成员方法访问操作：'' . get_class($obj) . '' -&gt; '' . $method . ''成员变量不具备callable特性，不能调用&lt;br&gt;'');</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; else{</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;//如果成员不存在</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;throw new DIException(get_class($obj) . "对象的方法名[ {$method} ]不存在");</div><div align="left">&nbsp; &nbsp;&nbsp; &nbsp; }</div><div align="left">}</div><br>\r\n<br>\r\n<div align="left">执行入口：</div><br>\r\n<div align="left">DI_ROUTE_REWRITE &amp;&amp; invoke_method($r = <strong>new </strong>DIRouteRewrite, ''rewrite'');&nbsp;&nbsp;</div><br>\r\n<br>\r\n<br>\r\n<font color="#1e347b"><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<font size="2">本文转载须经作者{折木}同意&lt; <font color="#336699"><u>http://www.kusha.biz/forum.php?mod=viewthread&amp;tid=3618&amp;page=1&amp;extra=#pid24752</u></font> &gt;</font><font size="1"><font color="#c0c0c0">(别看汉字没写多少，【【注释不是吗~~)</font></font><img src="http://www.kusha.biz/static/image/smiley/pansite/PST_119.gif" smilieid="105" border="0" alt=""><font size="1"><font color="#c0c0c0"> </font></font><br>\r\n<font color="#1e347b"><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<font color="#000"> </font><br>\r\n<font color="#1e347b"><br>\r\n</font><br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<font color="#000"><br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n<br>\r\n</font><br>\r\n<br>\r\n</td></tr></tbody></table>\r\n</div>\r\n<div id="comment_24752" class="cm">\r\n</div>\r\n<div id="post_rate_div_24752"></div>\r\n</div></div>\r\n\r\n</td>', 1429639311, 1429639311, 1, '', 99);
