(57,	'S1~S9子域名资源均衡',	'改进方案，免存过长的cookie\r\n\r\n<pre>\r\n<code>\r\n		$(function(){\r\n		    var that = this;\r\n		    that.getCookie = function(name) {\r\n				var arr = document.cookie.match(new RegExp(\"(^| )\" + name + \"=([^;]*)(;|$)\"));\r\n				if (arr != null) return decodeURI(arr[2]);\r\n				return null;\r\n			};\r\n			$(\'img\').each(function(i, e){\r\n			    if (! e.src.match(/^http\\:\\/\\/res\\.miku\\.us/)) return;\r\n		            var n = i % 9 + 1;\r\n	         	    e.src = e.src.replace(/(^http\\:\\/\\/)(res\\.miku\\.us)/, \'$1s\' + n + \'.$2\');\r\n		    });\r\n		});\r\n</code>\r\n</pre>',	'<pre>\r\n<code>\r\n$(function(){\r\n	var that = this;\r\n	that.getCookie = function(name) {\r\n		var arr = document.cookie.match(new RegExp(\"(^| )\" + name + \"=([^;]*)(;|$)\"));\r\n		if (arr != null) return decodeURI(arr[2]);\r\n		return null;\r\n	};\r\n	$(\'img\').each(function(i, e){\r\n		var key = e.src.replace(/http\\:\\/\\/res\\.miku\\.us/, \'\');\r\n		var n = that.getCookie(key) || (parseInt(Math.random() * 9) + 1);\r\n		document.cookie = key + \'=\' + n;\r\n		e.src = e.src.replace(/(^http\\:\\/\\/)(res\\.miku\\.us)/, \'$1s\' + n + \'.$2\');\r\n	});\r\n});\r\n</code>\r\n</pre>\r\n\r\n改进方案，免存过长的cookie\r\n\r\n<pre>\r\n<code>\r\n		$(function(){\r\n		    var that = this;\r\n		    that.getCookie = function(name) {\r\n				var arr = document.cookie.match(new RegExp(\"(^| )\" + name + \"=([^;]*)(;|$)\"));\r\n				if (arr != null) return decodeURI(arr[2]);\r\n				return null;\r\n			};\r\n			$(\'img\').each(function(i, e){\r\n			    if (! e.src.match(/^http\\:\\/\\/res\\.miku\\.us/)) return;\r\n		            var n = i % 9 + 1;\r\n	         	    e.src = e.src.replace(/(^http\\:\\/\\/)(res\\.miku\\.us)/, \'$1s\' + n + \'.$2\');\r\n		    });\r\n		});\r\n</code>\r\n</pre>',	'<pre>\r\n<code>\r\n$(function(){\r\n	var that = this;\r\n	that.getCookie = function(name) {\r\n		var arr = document.cookie.match(new RegExp(\"(^| )\" + name + \"=([^;]*)(;|$)\"));\r\n		if (arr != null) return decodeURI(arr[2]);\r\n		return null;\r\n	};\r\n	$(\'img\').each(function(i, e){\r\n		var key = e.src.replace(/http\\:\\/\\/res\\.miku\\.us/, \'\');\r\n		var n = that.getCookie(key) || (parseInt(Math.random() * 9) + 1);\r\n		document.cookie = key + \'=\' + n;\r\n		e.src = e.src.replace(/(^http\\:\\/\\/)(res\\.miku\\.us)/, \'$1s\' + n + \'.$2\');\r\n	});\r\n});\r\n</code>\r\n</pre>\r\n\r\n改进方案，免存过长的cookie\r\n\r\n<pre>\r\n<code>\r\n		$(function(){\r\n		    var that = this;\r\n		    that.getCookie = function(name) {\r\n				var arr = document.cookie.match(new RegExp(\"(^| )\" + name + \"=([^;]*)(;|$)\"));\r\n				if (arr != null) return decodeURI(arr[2]);\r\n				return null;\r\n			};\r\n			$(\'img\').each(function(i, e){\r\n			    if (! e.src.match(/^http\\:\\/\\/res\\.miku\\.us/)) return;\r\n		            var n = i % 9 + 1;\r\n	         	    e.src = e.src.replace(/(^http\\:\\/\\/)(res\\.miku\\.us)/, \'$1s\' + n + \'.$2\');\r\n		    });\r\n		});\r\n</code>\r\n</pre>',	1443845651,	1461416299,	1,	'',	0,	1),
(58,	'载入外部脚本的通用脚本',	'<pre>\r\n<code>\r\n! function(){\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\');//你的脚本\r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n}();\r\n</code>\r\n</pre>',	'<pre>\r\n\r\n<code>\r\n! function(){\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\');//你的脚本\r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n}();>\r\n\r\n<legend>实用示例：通过11830.tunnel.mobi代理访问bbs.sise.com.cn时，替换图片链接为正确的链接</legend>\r\n<div></div>\r\n<code>\r\n! function(){\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\'); \r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n	setInterval(function(){\r\n		$(\'img\').each(function(i, e){\r\n			e.src = e.src.replace(/(^http\\:\\/\\/)bbs\\.sise\\.com\\.cn/, \'$1\' + window.location.host); \r\n		});\r\n	}, 1000);\r\n}();\r\n\r\n\r\n\r\n\r\n\r\n加载jquery的靠谱版本：\r\n\r\nfunction loadJquery(win, onload){\r\n    if (win.jQuery) {\r\n	    var doc = win.document;\r\n		var je = doc.createElement(\"script\"); \r\n		je.setAttribute(\"type\", \"text/javascript\"); \r\n		je.setAttribute(\"src\", \"//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\");\r\n		var heads = doc.getElementsByTagName(\"head\"); \r\n		if (heads.length) {\r\n			heads[0].appendChild(je);\r\n		} else {\r\n			doc.documentElement.appendChild(je);\r\n		}\r\n    }\r\n	var iv = setInterval(function(){\r\n        win.jQuery && ! function(){\r\n            clearInterval(iv);\r\n            if (\'function\' == typeof onload) {\r\n                win.jQuery(function(){\r\n                    onload(win.jQuery);\r\n                });\r\n            }\r\n        }();\r\n    }, 10);\r\n}\r\n\r\n</code>\r\n\r\n</pre>',	'<pre>\r\n\r\n<code>\r\n! function(){\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\');//你的脚本\r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n}();>\r\n\r\n<legend>实用示例：通过11830.tunnel.mobi代理访问bbs.sise.com.cn时，替换图片链接为正确的链接</legend>\r\n<div></div>\r\n<code>\r\n! function(){\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\'); \r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n	setInterval(function(){\r\n		$(\'img\').each(function(i, e){\r\n			e.src = e.src.replace(/(^http\\:\\/\\/)bbs\\.sise\\.com\\.cn/, \'$1\' + window.location.host); \r\n		});\r\n	}, 1000);\r\n}();\r\n\r\n\r\n\r\n\r\n\r\n加载jquery的靠谱版本：\r\n\r\nfunction loadJquery(win, onload){\r\n    if (win.jQuery) {\r\n	    var doc = win.document;\r\n		var je = doc.createElement(\"script\"); \r\n		je.setAttribute(\"type\", \"text/javascript\"); \r\n		je.setAttribute(\"src\", \"//cdn.bootcss.com/jquery/2.1.4/jquery.min.js\");\r\n		var heads = doc.getElementsByTagName(\"head\"); \r\n		if (heads.length) {\r\n			heads[0].appendChild(je);\r\n		} else {\r\n			doc.documentElement.appendChild(je);\r\n		}\r\n    }\r\n	var iv = setInterval(function(){\r\n        win.jQuery && ! function(){\r\n            clearInterval(iv);\r\n            if (\'function\' == typeof onload) {\r\n                win.jQuery(function(){\r\n                    onload(win.jQuery);\r\n                });\r\n            }\r\n        }();\r\n    }, 10);\r\n}\r\n\r\n</code>\r\n\r\n</pre>',	1444365819,	1447915140,	1,	'',	0,	1),
(59,	'利用自调函数和setTimeout实现循环显示倒计时',	'<pre>\r\n<code>\r\n        var f, i = 0, z = 10;\r\n        (f = function(){ \r\n            button.css(\'background-color\',\'gray\').text(\'稍等\'+(z-i)+\'秒\'); i++;\r\n            if (i <= z) {\r\n                setTimeout(f, 1000); \r\n            } else {\r\n                button.css(\'background-color\',\'#FF9723\').text(\'评论\');\r\n            }\r\n        })();\r\n</code>\r\n</pre>',	'<pre>\r\n<code>\r\n        var f, i = 0, z = 10;\r\n        (f = function(){ \r\n            button.css(\'background-color\',\'gray\').text(\'稍等\'+(z-i)+\'秒\'); i++;\r\n            if (i <= z) {\r\n                setTimeout(f, 1000); \r\n            } else {\r\n                button.css(\'background-color\',\'#FF9723\').text(\'评论\');\r\n            }\r\n        })();\r\n</code>\r\n\r\n<img src=\"http://res.miku.us/res/img/default/2015/10/12/112702-422-hex21d.png\" width=\"100%\">\r\n\r\n</pre>\r\n\r\n\r\n',	'<pre>\r\n<code>\r\n        var f, i = 0, z = 10;\r\n        (f = function(){ \r\n            button.css(\'background-color\',\'gray\').text(\'稍等\'+(z-i)+\'秒\'); i++;\r\n            if (i <= z) {\r\n                setTimeout(f, 1000); \r\n            } else {\r\n                button.css(\'background-color\',\'#FF9723\').text(\'评论\');\r\n            }\r\n        })();\r\n</code>\r\n\r\n<img src=\"http://res.miku.us/res/img/default/2015/10/12/112702-422-hex21d.png\" width=\"100%\">\r\n\r\n</pre>\r\n\r\n\r\n',	1444620484,	1467479666,	1,	'',	1,	1),
(60,	'利用smarty将PHP的数组转换为页面中的json表达式',	'<pre>\r\n<code>\r\n假定smarty的左右边界符为“<{”和“}>”\r\n\r\n1、使用$smarty->assgin(\'request\', $_REQUEST)把HTTP的请求内容以数组形式传递到模板变量request\r\n\r\n2、在smarty模板中，利用语句 <{json_encode($request) nofilter}>  即可打印出json的表达式。\r\n\r\n例如： \r\n<script>\r\nconsole.log(<{json_encode($request) nofilter}>)\r\n</script>\r\n</code>\r\n</pre>',	'<pre>\r\n<code>\r\n假定smarty的左右边界符为“<{”和“}>”\r\n\r\n1、使用$smarty->assgin(\'request\', $_REQUEST)把HTTP的请求内容以数组形式传递到模板变量request\r\n\r\n2、在smarty模板中，利用语句 <{json_encode($request) nofilter}>  即可打印出json的表达式。\r\n\r\n例如： \r\n<script>\r\nconsole.log(<{json_encode($request) nofilter}>)\r\n</script>\r\n</code>\r\n</pre>',	'<pre>\r\n<code>\r\n假定smarty的左右边界符为“<{”和“}>”\r\n\r\n1、使用$smarty->assgin(\'request\', $_REQUEST)把HTTP的请求内容以数组形式传递到模板变量request\r\n\r\n2、在smarty模板中，利用语句 <{json_encode($request) nofilter}>  即可打印出json的表达式。\r\n\r\n例如： \r\n<script>\r\nconsole.log(<{json_encode($request) nofilter}>)\r\n</script>\r\n</code>\r\n</pre>',	1444648750,	1444712513,	1,	'',	3,	1),
(61,	'金馆鱼 Plus',	'<input value=\"100\"><button id=\"posts-61\">发动特技</button>\r\n<script>\r\n$(function(){\r\n\r\nfunction loadJinguanyu(max){\r\n        if (max) {\r\n             if (location.href.match(/\\#jinguanyu\\=/)) {\r\n                   location.href.replace(/\\#jinguanyu\\=(\\w|\\d)+/, \'#jinguanyu=\'+max);\r\n             } else {\r\n                  location.href += \'#jinguanyu=\' + max;\r\n             }\r\n        }\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'http://miku.us/res/lib/anime.js?t=\'+(-(-new Date())));\r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n};\r\n\r\n$(\'#posts-61\').parents(\'[data-id=61]\').find(\'*\').unbind(\'click\');\r\n$(\'#posts-61\').click(function(){\r\n    var max = $(this).prev(\'input\').val();\r\n    loadJinguanyu(max);\r\n});\r\n\r\n})\r\n</script>',	'<input value=\"100\"><button id=\"posts-61\">发动特技</button>\r\n<script>\r\n$(function(){\r\n\r\nfunction loadJinguanyu(max){\r\n        if (max) {\r\n             if (location.href.match(/\\#jinguanyu\\=/)) {\r\n                   location.href.replace(/\\#jinguanyu\\=(\\w|\\d)+/, \'#jinguanyu=\'+max);\r\n             } else {\r\n                  location.href += \'#jinguanyu=\' + max;\r\n             }\r\n        }\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'http://miku.us/res/lib/anime.js?t=\'+(-(-new Date())));\r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n};\r\n\r\n$(\'#posts-61\').parents(\'[data-id=61]\').find(\'*\').unbind(\'click\');\r\n$(\'#posts-61\').click(function(){\r\n    var max = $(this).prev(\'input\').val();\r\n    loadJinguanyu(max);\r\n});\r\n\r\n})\r\n</script>',	'<input value=\"100\"><button id=\"posts-61\">发动特技</button>\r\n<script>\r\n$(function(){\r\n\r\nfunction loadJinguanyu(max){\r\n        if (max) {\r\n             if (location.href.match(/\\#jinguanyu\\=/)) {\r\n                   location.href.replace(/\\#jinguanyu\\=(\\w|\\d)+/, \'#jinguanyu=\'+max);\r\n             } else {\r\n                  location.href += \'#jinguanyu=\' + max;\r\n             }\r\n        }\r\n	var doc = document; \r\n	var je = doc.createElement(\"script\"); \r\n	je.setAttribute(\"type\", \"text/javascript\"); \r\n	je.setAttribute(\"src\", \'http://miku.us/res/lib/anime.js?t=\'+(-(-new Date())));\r\n	var heads = doc.getElementsByTagName(\"head\"); \r\n	if (heads.length) {\r\n		heads[0].appendChild(je);\r\n	} else {\r\n		doc.documentElement.appendChild(je);\r\n	}\r\n};\r\n\r\n$(\'#posts-61\').parents(\'[data-id=61]\').find(\'*\').unbind(\'click\');\r\n$(\'#posts-61\').click(function(){\r\n    var max = $(this).prev(\'input\').val();\r\n    loadJinguanyu(max);\r\n});\r\n\r\n})\r\n</script>',	1446805707,	1446808768,	1,	'',	0,	1),
(62,	'GMO服务吉祥物 - Anzu & Conoha',	'<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193346-426-hex11a.jpg\'><br>\r\n\r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193435-475-hex1f3.jpg\'><br>\r\n\r\n<pre>\r\n\r\n<code>\r\n\r\nhttp://cloud.gmo.jp/anzu/\r\n\r\n名前：美雲あんず -みくもあんず-\r\n年齢：人間でいうところの13歳くらい？\r\n身長：150cm体重：？？\r\n長所：明るく好奇心旺盛！ 何事にも前向きに頑張るところ\r\n短所：おっちょこちょいなところ\r\n好きなもの：あんずジャムがたっぷり入ったアップルパイ\r\n\r\n\r\n東北地方ののどかな田舎町の、とあるりんご農家で座敷童子として生まれ、のんびりと幼少時代を過ごす。\r\n\r\n\r\n１２歳を迎えた頃、代々の掟に倣い上京！渋谷に降り立つ。雲の上の神さまになれる♪と思って飛び込んだそこは、「クラウドコンピューティング」の世界！右も左もわからないまま、現在はGMOインターネットのデータセンターに住みつき目下勉強中。\r\n\r\n</code>\r\n\r\n</pre>',	'<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193346-426-hex11a.jpg\'><br>\r\n\r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193435-475-hex1f3.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193522-522-hex127.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193541-541-hex141.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193611-571-hex194.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193629-589-hex3a1.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193701-621-hexe9.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/193723-643-hex3b3.jpg\'><br>\r\n     \r\n<img style=\"width:90%;\" src=\'http://res.miku.us/res/img/default/2015/11/08/194004-804-hex3d5.jpg\'><br>\r\n\r\n\r\n<pre>\r\n\r\n<code>\r\n\r\nhttp://cloud.gmo.jp/anzu/\r\n\r\n名前：美雲あんず -みくもあんず-\r\n年齢：人間でいうところの13歳くらい？\r\n身長：150cm体重：？？\r\n長所：明るく好奇心旺盛！ 何事にも前向きに頑張るところ\r\n短所：おっちょこちょいなところ\r\n好きなもの：あんずジャムがたっぷり入ったアップルパイ\r\n\r\n\r\n東北地方ののどかな田舎町の、とあるりんご農家で座敷童子として生まれ、のんびりと幼少時代を過ごす。